import React, { Component } from 'react';
import './SettingsDropDown.css';

class SettingsDropDown extends Component {
    render() {
        return (
            <ul className="settings-dropdown">
                <li>
                    <h3 className="settings-dropdown-title">New Tab</h3>
                    <h3 className="settings-dropdown-shortcut">Ctrl+T</h3>
                </li>
                <li>
                    <h3 className="settings-dropdown-title">New Window</h3>
                    <h3 className="settings-dropdown-shortcut">Ctrl+N</h3>
                </li>
                <li>
                    <h3 className="settings-dropdown-title">Create New Folder</h3>
                    <h3 className="settings-dropdown-shortcut">Shift+Ctrl+N</h3>
                </li>
                <li>
                    <h3 className="settings-dropdown-title">Create New Document</h3>
                </li>
                <li>
                    <h3 className="settings-dropdown-title">Close All Windows</h3>
                </li>
                <li>
                    <h3 className="settings-dropdown-title">Close</h3>
                </li>
                {this.props.data.map((item) => {
                    if (item.title && item.shortcut) {
                        return (
                            <li onClick={item.handleClick}>
                                <h3 className="settings-dropdown-title">{item.title}</h3>
                                <h3 className="settings-dropdown-shortcut">{item.shortcut}</h3>
                            </li>
                        )
                    }else{
                        return (
                            <li onClick={item.handleClick}>
                                <h3 className="settings-dropdown-title">{item.title}</h3>
                            </li>
                        )
                    }
                })}
            </ul>
        );
    }
}

export default SettingsDropDown;
