import React, { Component } from 'react';
import BackgroundImage from "../../media/background.jpg";
import './HomeScreen.css';

class HomeScreen extends Component {
    constructor() {
        super();
        this.firstPos = {
            x: 0,
            y: 0
        }
    }
    componentDidMount() {
        document.getElementById("HomeScreen").addEventListener("mouseup", this.onMouseUp);
        document.getElementById("HomeScreen").addEventListener("mousedown", this.onMouseDown);
    }
    onMouseUp = () =>{
        console.log("mouse up");
        document.removeEventListener('mousemove', this.drawBox);
        document.getElementById("selectBox").remove();
    }
    onMouseDown = (e) => {
        // this.setState({
        //     firstPos: {
        //         x: e.pageX,
        //         y: e.pageY
        //     }
        // });
        firstPos = {  x: e.pageX,   y: e.pageY}
        console.log("mouse down", e);
        document.addEventListener('mousemove', this.drawBox);
    }
    drawBox = (e) => {
        console.log("working");
        let secondPos = {
            x: e.pageX,
            y: e.pageY
        }
        let posDiff = {
            x:firstPos.x - secondPos.x,
            y:firstPos.y - secondPos.y,
        }
        let size = {
            w: Math.abs(e.pageX -firstPos.x),
            h: Math.abs(e.pageY -firstPos.y)
        }
        let box = document.getElementById("selectBox");
        if (box === null) {
            box = document.createElement("div");
            box.id = "selectBox";
            box.style.width = "0px";
            box.style.height = "0px";
            box.style.position = "absolute";
            box.style.top =firstPos.y + "px";
            box.style.left =firstPos.x + "px";
            document.getElementById("HomeScreen").appendChild(box);
        }
        box.style.width = size.w + "px";
        box.style.height = size.h + "px";
        if (posDiff.x > 0) {
            box.style.left =firstPos.x - posDiff.x + "px";
        }
        if (posDiff.y > 0) {
            box.style.top =firstPos.y - posDiff.y + "px";
        }
        console.log(box.style.width);
    }
    render() {
        return (
            <div id="HomeScreen" style={{ backgroundImage: `url(${BackgroundImage})` }}>

            </div>
        );
    }
}

export default HomeScreen;
